import React, { useEffect, useState, useCallback } from 'react';
import axios from 'axios';
import { toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const Dashboard = () => {
  const token = localStorage.getItem('token');
  const [publishedSessions, setPublishedSessions] = useState([]);
  const [mySessions, setMySessions] = useState([]);
  const [form, setForm] = useState({ title: '', tags: '', json_file_url: '' });
  const [draftSessionId, setDraftSessionId] = useState(null);
  const [isSaving, setIsSaving] = useState(false);
  const [saveTimeout, setSaveTimeout] = useState(null);

  // Fetch Public Sessions
  const fetchPublished = async () => {
    try {
      const res = await axios.get('http://localhost:8000/sessions');
      setPublishedSessions(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  // Fetch My Sessions
  const fetchMySessions = useCallback(async () => {
    try {
      const res = await axios.get('http://localhost:8000/sessions/my-sessions', {
        headers: { Authorization: `Bearer ${token}` },
      });
      setMySessions(res.data);
    } catch (err) {
      console.error(err);
    }
  }, [token]);

  // Save as Draft
  const saveDraft = async () => {
    setIsSaving(true);
    try {
      const res = await axios.post(
        'http://localhost:8000/sessions/my-sessions/save-draft',
        form,
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setDraftSessionId(res.data._id);
      toast.success('Auto-saved draft ✅');
    } catch (err) {
      toast.error('Auto-save failed ❌');
      console.error(err);
    }
    setIsSaving(false);
  };

  // Publish Session
  const publishSession = async () => {
    if (!draftSessionId) return toast.error('No draft to publish!');
    try {
      await axios.post(
        'http://localhost:8000/sessions/my-sessions/publish',
        { id: draftSessionId },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      toast.success('Published ✅');
      setForm({ title: '', tags: '', json_file_url: '' });
      setDraftSessionId(null);
      fetchPublished();
      fetchMySessions();
    } catch (err) {
      toast.error('Publish failed ❌');
    }
  };

  // Auto-save on inactivity (5s)
  useEffect(() => {
    if (!form.title && !form.tags && !form.json_file_url) return;
    if (saveTimeout) clearTimeout(saveTimeout);
    const timeout = setTimeout(saveDraft, 5000);
    setSaveTimeout(timeout);
    return () => clearTimeout(timeout);
  }, [form]); // eslint-disable-line react-hooks/exhaustive-deps

  useEffect(() => {
    fetchPublished();
    fetchMySessions();
  }, [fetchMySessions]);

  return (
    <div className="p-6 md:p-10 bg-gradient-to-br from-green-50 to-green-100 min-h-screen font-sans text-gray-800">
      <h1 className="text-3xl md:text-4xl font-bold mb-8 text-green-800 animate-pulse">🌿 Dashboard</h1>

      {/* SESSION FORM */}
      <div className="bg-white rounded-lg shadow-md p-6 mb-10 transition-all hover:shadow-xl">
        <h2 className="text-xl font-semibold mb-4">Create / Edit Session</h2>
        <input
          type="text"
          placeholder="📝 Title"
          className="w-full mb-3 p-2 border border-green-300 rounded"
          value={form.title}
          onChange={(e) => setForm({ ...form, title: e.target.value })}
        />
        <input
          type="text"
          placeholder="🏷️ Tags (comma-separated)"
          className="w-full mb-3 p-2 border border-green-300 rounded"
          value={form.tags}
          onChange={(e) => setForm({ ...form, tags: e.target.value })}
        />
        <input
          type="text"
          placeholder="🔗 JSON File URL"
          className="w-full mb-3 p-2 border border-green-300 rounded"
          value={form.json_file_url}
          onChange={(e) => setForm({ ...form, json_file_url: e.target.value })}
        />
        <div className="flex gap-4">
          <button
            className="bg-green-500 hover:bg-green-600 text-white py-2 px-4 rounded"
            onClick={saveDraft}
          >
            💾 Save as Draft
          </button>
          <button
            className="bg-blue-500 hover:bg-blue-600 text-white py-2 px-4 rounded"
            onClick={publishSession}
          >
            🚀 Publish
          </button>
        </div>
        {isSaving && <p className="text-sm text-green-600 mt-2 animate-pulse">Saving draft...</p>}
      </div>

      {/* MY SESSIONS */}
      <div className="mb-10">
        <h2 className="text-2xl font-semibold mb-4 text-green-700">🗂️ My Sessions</h2>
        <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-4">
          {mySessions.map((s) => (
            <div
              key={s._id}
              className="bg-white p-4 rounded shadow border hover:border-green-500 transition-all"
            >
              <h3 className="font-bold text-lg">{s.title}</h3>
              <p className="text-sm text-gray-600">{s.tags?.join(', ')}</p>
              <p className="text-xs text-gray-500 mt-2">Status: {s.status}</p>
            </div>
          ))}
        </div>
      </div>

      {/* PUBLISHED SESSIONS */}
      <div>
        <h2 className="text-2xl font-semibold mb-4 text-green-700">🌍 Public Sessions</h2>
        <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-4">
          {publishedSessions.map((s) => (
            <div
              key={s._id}
              className="bg-white p-4 rounded shadow border hover:border-green-500 transition-all"
            >
              <h3 className="font-bold text-lg">{s.title}</h3>
              <p className="text-sm text-gray-600">{s.tags?.join(', ')}</p>
              <a
                href={s.json_file_url}
                className="text-blue-600 text-sm underline"
                target="_blank"
                rel="noreferrer"
              >
                View JSON
              </a>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
