import React, { useEffect, useState } from "react";
import axios from "axios";
import SessionCard from "./SessionCard";

export default function Dashboard() {
  const [sessions, setSessions] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [title, setTitle] = useState("");
  const [tags, setTags] = useState("");
  const [url, setUrl] = useState("");

  const token = localStorage.getItem("token");

  const fetchSessions = async () => {
    try {
      const res = await axios.get("http://localhost:8000/sessions/my-sessions", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setSessions(res.data);
    } catch (err) {
      console.error("Error fetching sessions:", err);
    }
  };

  const handleCreateSession = async (e) => {
    e.preventDefault();
    try {
      await axios.post(
        "http://localhost:8000/sessions/my-sessions/save-draft",
        {
          title,
          tags,
          json_file_url: url,
        },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      setTitle("");
      setTags("");
      setUrl("");
      setShowModal(false);
      fetchSessions(); // refresh
    } catch (err) {
      alert("❌ Failed to create session");
    }
  };

  useEffect(() => {
    fetchSessions();
  }, []);

  return (
    <div className="min-h-screen bg-green-50 p-6">
      <div className="max-w-4xl mx-auto">
        <div className="flex justify-between items-center mb-6">
          <h1 className="text-3xl font-bold text-green-700">📋 My Sessions</h1>
          <button
            onClick={() => setShowModal(true)}
            className="bg-green-700 text-white px-4 py-2 rounded hover:bg-green-800"
          >
            + Create Session
          </button>
        </div>

        {/* Session Cards */}
        <div className="grid md:grid-cols-2 gap-4">
          {sessions.map((s) => (
            <SessionCard key={s._id} session={s} fetchSessions={fetchSessions} />
          ))}
        </div>

        {/* Modal */}
        {showModal && (
          <div className="fixed inset-0 bg-black/40 flex justify-center items-center z-50">
            <div className="bg-white p-6 rounded-lg w-full max-w-md space-y-4 shadow-lg">
              <h2 className="text-xl font-semibold text-green-700">New Session</h2>
              <form onSubmit={handleCreateSession} className="space-y-3">
                <input
                  type="text"
                  placeholder="Title"
                  value={title}
                  onChange={(e) => setTitle(e.target.value)}
                  required
                  className="w-full border border-gray-300 p-2 rounded"
                />
                <input
                  type="text"
                  placeholder="Tags (comma separated)"
                  value={tags}
                  onChange={(e) => setTags(e.target.value)}
                  className="w-full border border-gray-300 p-2 rounded"
                />
                <input
                  type="url"
                  placeholder="JSON File URL"
                  value={url}
                  onChange={(e) => setUrl(e.target.value)}
                  className="w-full border border-gray-300 p-2 rounded"
                />
                <div className="flex justify-end gap-2">
                  <button
                    type="button"
                    onClick={() => setShowModal(false)}
                    className="px-3 py-1 bg-gray-300 rounded"
                  >
                    Cancel
                  </button>
                  <button
                    type="submit"
                    className="px-4 py-2 bg-green-600 text-white rounded hover:bg-green-700"
                  >
                    Save Draft
                  </button>
                </div>
              </form>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}
