// src/pages/MySessions.js
import React, { useEffect, useState, useCallback } from "react";
import Navbar from "../components/Navbar";
import Footer from "../components/Footer";
import { motion, AnimatePresence } from "framer-motion";
import { FaEdit, FaTrash, FaUpload, FaExternalLinkAlt, FaTimes } from "react-icons/fa";

const MySessions = () => {
  const [sessions, setSessions] = useState([]);
  const [mobileMenuOpen, setMobileMenuOpen] = useState(false);
  const [loading, setLoading] = useState(true);
  const [confirmModal, setConfirmModal] = useState(null);
  const [editingSession, setEditingSession] = useState(null); // session object
  const [editTitle, setEditTitle] = useState("");
  const [editTags, setEditTags] = useState("");
  const [editJsonUrl, setEditJsonUrl] = useState("");
  const [saving, setSaving] = useState(false);
  const [message, setMessage] = useState("");

  const token = localStorage.getItem("token");

  const fetchSessions = useCallback(async () => {
    try {
      setLoading(true);
      const res = await fetch("http://localhost:8000/sessions/my-sessions", {
        headers: { Authorization: `Bearer ${token}` },
      });
      const data = await res.json();
      setSessions(data);
    } catch (err) {
      console.error("Error fetching sessions:", err);
    } finally {
      setLoading(false);
    }
  }, [token]);

  useEffect(() => {
    fetchSessions();
  }, [fetchSessions]);

  const deleteSession = async (id) => {
    try {
      await fetch(`http://localhost:8000/sessions/my-sessions/${id}`, {
        method: "DELETE",
        headers: { Authorization: `Bearer ${token}` },
      });
      setConfirmModal(null);
      fetchSessions();
    } catch (err) {
      console.error("Error deleting session:", err);
    }
  };

  const publishSession = async (id) => {
    try {
      await fetch("http://localhost:8000/sessions/my-sessions/publish", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ id }),
      });
      setConfirmModal(null);
      fetchSessions();
    } catch (err) {
      console.error("Error publishing session:", err);
    }
  };

  const handleEditClick = (session) => {
    setEditingSession(session);
    setEditTitle(session.title);
    setEditTags(session.tags.join(", "));
    setEditJsonUrl(session.json_file_url);
    setMessage("");
  };

  const handleCancelEdit = () => {
    setEditingSession(null);
  };

  const handleSaveDraft = async () => {
    if (!editTitle || !editTags || !editJsonUrl) {
      setMessage("⚠ Please fill all fields before saving.");
      return;
    }
    setSaving(true);
    try {
      const res = await fetch(`http://localhost:8000/sessions/my-sessions/${editingSession._id}`, {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({
          title: editTitle,
          tags: editTags.split(",").map((t) => t.trim()),
          json_file_url: editJsonUrl,
        }),
      });
      const data = await res.json();
      if (res.ok) {
        setMessage("✅ Draft updated successfully!");
        fetchSessions();
        setTimeout(() => setEditingSession(null), 800);
      } else {
        setMessage(`❌ ${data.msg || "Failed to update draft"}`);
      }
    } catch (err) {
      setMessage("❌ Something went wrong.");
    }
    setSaving(false);
  };

  const handlePublish = async () => {
    await handleSaveDraft(); // update first
    publishSession(editingSession._id);
    setEditingSession(null);
  };

  const SessionCard = ({ session, isDraft }) => (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      className="bg-white rounded-xl shadow-md overflow-hidden hover:shadow-xl transition transform hover:-translate-y-1"
    >
      <div className="bg-gradient-to-r from-green-600 to-emerald-500 p-4 flex justify-between items-center">
        <h3 className="text-lg font-semibold text-white truncate">{session.title}</h3>
        <span
          className={`text-xs px-3 py-1 rounded-full ${
            isDraft ? "bg-yellow-200 text-yellow-800" : "bg-green-200 text-green-800"
          }`}
        >
          {isDraft ? "Draft" : "Published"}
        </span>
      </div>

      <div className="p-5 space-y-4">
        <div className="flex flex-wrap gap-2">
          {session.tags.map((tag, i) => (
            <span
              key={i}
              className="px-3 py-1 bg-green-100 text-green-700 rounded-full text-xs font-medium"
            >
              #{tag}
            </span>
          ))}
        </div>

        <div className="flex flex-wrap gap-2">
          <a
            href={session.json_file_url}
            target="_blank"
            rel="noreferrer"
            className="flex-1 flex items-center justify-center gap-2 px-4 py-2 bg-blue-600 hover:bg-blue-700 text-white rounded-lg transition"
          >
            <FaExternalLinkAlt /> View
          </a>
          <button
            onClick={() => handleEditClick(session)}
            className="flex-1 flex items-center justify-center gap-2 px-4 py-2 bg-yellow-500 hover:bg-yellow-600 text-white rounded-lg transition"
          >
            <FaEdit /> Edit
          </button>
          <button
            onClick={() => setConfirmModal({ id: session._id, action: "delete" })}
            className="flex-1 flex items-center justify-center gap-2 px-4 py-2 bg-red-500 hover:bg-red-600 text-white rounded-lg transition"
          >
            <FaTrash /> Delete
          </button>
          {isDraft && (
            <button
              onClick={() => setConfirmModal({ id: session._id, action: "publish" })}
              className="flex-1 flex items-center justify-center gap-2 px-4 py-2 bg-green-600 hover:bg-green-700 text-white rounded-lg transition"
            >
              <FaUpload /> Publish
            </button>
          )}
        </div>
      </div>
    </motion.div>
  );

  const drafts = sessions.filter((s) => s.status === "draft");
  const published = sessions.filter((s) => s.status === "published");

  return (
    <div className={`bg-gray-50 min-h-screen flex flex-col ${editingSession ? "blur-sm pointer-events-none" : ""}`}>
      <Navbar onMobileMenuToggle={setMobileMenuOpen} />
      <motion.div
        animate={{ marginTop: mobileMenuOpen ? 220 : 64 }}
        transition={{ duration: 0.3 }}
        className="flex-1"
      >
        <div className="max-w-6xl mx-auto px-4 py-10">
          <h1 className="text-3xl font-bold text-green-700 mb-8 text-center">
            📂 My Sessions
          </h1>
          {loading ? (
            <p className="text-center text-gray-500">Loading sessions...</p>
          ) : (
            <>
              <h2 className="text-2xl font-semibold mb-4">✅ Published</h2>
              {published.length === 0 ? (
                <p className="text-gray-500 mb-6">No published sessions yet.</p>
              ) : (
                <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6 mb-10">
                  {published.map((session) => (
                    <SessionCard key={session._id} session={session} />
                  ))}
                </div>
              )}
              <h2 className="text-2xl font-semibold mb-4">📝 Drafts</h2>
              {drafts.length === 0 ? (
                <p className="text-gray-500">No draft sessions yet.</p>
              ) : (
                <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
                  {drafts.map((session) => (
                    <SessionCard key={session._id} session={session} isDraft />
                  ))}
                </div>
              )}
            </>
          )}
        </div>
      </motion.div>
      <Footer />

      {/* EDIT MODAL */}
      <AnimatePresence>
        {editingSession && (
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            className="fixed inset-0 z-50 flex items-center justify-center bg-black/60 backdrop-blur-sm"
          >
            <motion.div
              initial={{ scale: 0.9 }}
              animate={{ scale: 1 }}
              exit={{ scale: 0.9 }}
              className="bg-white rounded-xl shadow-xl w-11/12 max-w-lg p-6 space-y-4"
            >
              <div className="flex justify-between items-center border-b pb-3">
                <h2 className="text-xl font-bold text-green-700">Edit Session</h2>
                <button onClick={handleCancelEdit} className="text-gray-500 hover:text-gray-800">
                  <FaTimes size={20} />
                </button>
              </div>
              <div>
                <label className="block text-gray-700 font-medium mb-2">Session Title</label>
                <input
                  type="text"
                  value={editTitle}
                  onChange={(e) => setEditTitle(e.target.value)}
                  className="w-full border rounded-lg px-4 py-2 focus:outline-none focus:border-green-500"
                />
              </div>
              <div>
                <label className="block text-gray-700 font-medium mb-2">Tags</label>
                <input
                  type="text"
                  value={editTags}
                  onChange={(e) => setEditTags(e.target.value)}
                  className="w-full border rounded-lg px-4 py-2 focus:outline-none focus:border-green-500"
                />
              </div>
              <div>
                <label className="block text-gray-700 font-medium mb-2">JSON File URL</label>
                <input
                  type="text"
                  value={editJsonUrl}
                  onChange={(e) => setEditJsonUrl(e.target.value)}
                  className="w-full border rounded-lg px-4 py-2 focus:outline-none focus:border-green-500"
                />
              </div>
              {message && (
                <div className={`p-3 rounded-lg text-sm ${message.startsWith("✅") ? "bg-green-100 text-green-700" : "bg-red-100 text-red-700"}`}>
                  {message}
                </div>
              )}
              <div className="flex justify-end gap-3 pt-3 border-t">
                <button
                  onClick={handleCancelEdit}
                  className="px-4 py-2 bg-gray-300 rounded-lg hover:bg-gray-400"
                >
                  Cancel
                </button>
                <button
                  onClick={handleSaveDraft}
                  disabled={saving}
                  className="px-4 py-2 bg-yellow-500 text-white rounded-lg hover:bg-yellow-600 disabled:opacity-50"
                >
                  {saving ? "Saving..." : "Save Draft"}
                </button>
                <button
                  onClick={handlePublish}
                  disabled={saving}
                  className="px-4 py-2 bg-green-600 text-white rounded-lg hover:bg-green-700 disabled:opacity-50"
                >
                  {saving ? "Publishing..." : "Publish"}
                </button>
              </div>
            </motion.div>
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
};

export default MySessions;
