// src/pages/Dashboard.js
import { useEffect, useState } from "react";
import axios from "axios";
import SessionCard from "../components/SessionCard";

export default function Dashboard() {
  const [sessions, setSessions] = useState([]);
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [showModal, setShowModal] = useState(false);

  const fetchSessions = async () => {
    try {
      const token = localStorage.getItem("token");
      const res = await axios.get("http://localhost:8000/sessions/my-sessions", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setSessions(res.data);
    } catch (err) {
      alert("âŒ Failed to fetch sessions");
    }
  };

  const handleCreateSession = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem("token");
      await axios.post(
        "http://localhost:8000/sessions",
        { title, description },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setTitle("");
      setDescription("");
      setShowModal(false);
      fetchSessions();
    } catch (err) {
      alert("âŒ Failed to create session");
    }
  };

  useEffect(() => {
    fetchSessions();
  }, []);

  return (
    <div className="min-h-screen bg-gradient-to-br from-green-400 to-emerald-600 p-4 text-white">
      <div className="max-w-6xl mx-auto">
        {/* Header */}
        <div className="flex justify-between items-center mb-8 animate-fade-in-down">
          <h1 className="text-4xl font-extrabold">ðŸŽ¯ Your Sessions</h1>
          <button
            onClick={() => setShowModal(true)}
            className="bg-white text-green-700 font-semibold px-5 py-2 rounded-xl shadow-lg hover:bg-gray-100 transition-all"
          >
            + Create Session
          </button>
        </div>

        {/* Sessions Grid */}
        <div className="grid gap-6 sm:grid-cols-2 lg:grid-cols-3 animate-fade-in-up">
          {sessions.length > 0 ? (
            sessions.map((session) => (
              <SessionCard key={session._id} session={session} />
            ))
          ) : (
            <p className="col-span-full text-lg text-center">No sessions yet. Start one!</p>
          )}
        </div>
      </div>

      {/* Create Modal */}
      {showModal && (
        <div className="fixed inset-0 bg-black/40 backdrop-blur-sm flex items-center justify-center z-50 animate-fade-in">
          <form
            onSubmit={handleCreateSession}
            className="bg-white text-black p-6 rounded-2xl w-96 shadow-xl"
          >
            <h2 className="text-xl font-bold mb-4">Create a New Session</h2>
            <input
              type="text"
              placeholder="Session Title"
              value={title}
              onChange={(e) => setTitle(e.target.value)}
              className="w-full px-4 py-2 mb-3 border rounded-lg"
              required
            />
            <textarea
              placeholder="Description"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              className="w-full px-4 py-2 mb-4 border rounded-lg"
              rows={4}
              required
            ></textarea>
            <div className="flex justify-end gap-3">
              <button
                type="button"
                onClick={() => setShowModal(false)}
                className="px-4 py-2 bg-gray-200 rounded-lg"
              >
                Cancel
              </button>
              <button
                type="submit"
                className="px-4 py-2 bg-green-600 text-white rounded-lg"
              >
                Save
              </button>
            </div>
          </form>
        </div>
      )}
    </div>
  );
}
