// src/pages/SessionEditor.js
import React, { useState } from "react";
import Navbar from "../components/Navbar";
import Footer from "../components/Footer";
import { motion } from "framer-motion";

export default function SessionEditor() {
  const [title, setTitle] = useState("");
  const [tags, setTags] = useState("");
  const [jsonFileUrl, setJsonFileUrl] = useState("");
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState("");
  const [mobileMenuOpen, setMobileMenuOpen] = useState(false);

  const token = localStorage.getItem("token");

  // Save Draft
  const handleSaveDraft = async () => {
    if (!title || !tags || !jsonFileUrl) {
      setMessage("⚠ Please fill all fields before saving.");
      return;
    }
    setLoading(true);
    setMessage("");

    try {
      const res = await fetch("http://localhost:8000/sessions/my-sessions/save-draft", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({
          title,
          tags,
          json_file_url: jsonFileUrl,
        }),
      });

      const data = await res.json();
      if (res.ok) {
        setMessage("✅ Draft saved successfully!");
        resetForm();
      } else {
        setMessage(`❌ ${data.msg || "Failed to save draft"}`);
      }
    } catch (err) {
      setMessage("❌ Something went wrong. Please try again.");
    }
    setLoading(false);
  };

  // Publish Session
  const handlePublish = async () => {
    if (!title || !tags || !jsonFileUrl) {
      setMessage("⚠ Please fill all fields before publishing.");
      return;
    }
    setLoading(true);
    setMessage("");

    try {
      // First save draft
      const draftRes = await fetch("http://localhost:8000/sessions/my-sessions/save-draft", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({
          title,
          tags,
          json_file_url: jsonFileUrl,
        }),
      });

      const draftData = await draftRes.json();
      if (!draftRes.ok) {
        setMessage(`❌ ${draftData.msg || "Failed to save before publishing"}`);
        setLoading(false);
        return;
      }

      // Then publish using the draft's ID
      const publishRes = await fetch("http://localhost:8000/sessions/my-sessions/publish", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ id: draftData._id }),
      });

      if (publishRes.ok) {
        setMessage("✅ Session published successfully!");
        resetForm();
      } else {
        const publishData = await publishRes.json();
        setMessage(`❌ ${publishData.msg || "Failed to publish session"}`);
      }
    } catch (err) {
      setMessage("❌ Something went wrong. Please try again.");
    }
    setLoading(false);
  };

  const resetForm = () => {
    setTitle("");
    setTags("");
    setJsonFileUrl("");
  };

  return (
    <div className="bg-gray-50 min-h-screen flex flex-col">
      <Navbar onMobileMenuToggle={setMobileMenuOpen} />

      <motion.div
        animate={{ marginTop: mobileMenuOpen ? 220 : 64 }}
        transition={{ duration: 0.3 }}
        className="flex-1 max-w-3xl mx-auto px-4 py-10"
      >
        <h1 className="text-3xl font-bold text-green-700 mb-8 text-center">
          ✏ Create a New Session
        </h1>

        <div className="bg-white shadow-lg rounded-xl p-8 space-y-6 border border-gray-200">
          {/* Title */}
          <div>
            <label className="block text-gray-700 font-medium mb-2">Session Title</label>
            <input
              type="text"
              value={title}
              onChange={(e) => setTitle(e.target.value)}
              placeholder="Enter session title"
              className="w-full border border-gray-300 rounded-lg px-4 py-2 focus:outline-none focus:border-green-500"
            />
          </div>

          {/* Tags */}
          <div>
            <label className="block text-gray-700 font-medium mb-2">Tags</label>
            <input
              type="text"
              value={tags}
              onChange={(e) => setTags(e.target.value)}
              placeholder="e.g. yoga, meditation, wellness"
              className="w-full border border-gray-300 rounded-lg px-4 py-2 focus:outline-none focus:border-green-500"
            />
            <p className="text-xs text-gray-500 mt-1">Separate tags with commas.</p>
          </div>

          {/* JSON File URL */}
          <div>
            <label className="block text-gray-700 font-medium mb-2">JSON File URL</label>
            <input
              type="text"
              value={jsonFileUrl}
              onChange={(e) => setJsonFileUrl(e.target.value)}
              placeholder="Enter JSON file link"
              className="w-full border border-gray-300 rounded-lg px-4 py-2 focus:outline-none focus:border-green-500"
            />
          </div>

          {/* Message */}
          {message && (
            <motion.div
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              className={`p-3 rounded-lg text-sm ${
                message.startsWith("✅") ? "bg-green-100 text-green-700" : "bg-red-100 text-red-700"
              }`}
            >
              {message}
            </motion.div>
          )}

          {/* Action Buttons */}
          <div className="flex flex-wrap justify-center gap-4">
            <button
              onClick={handleSaveDraft}
              disabled={loading}
              className="flex-1 min-w-[140px] px-6 py-3 bg-yellow-500 hover:bg-yellow-600 text-white font-medium rounded-lg transition disabled:opacity-50"
            >
              {loading ? "Saving..." : "💾 Save as Draft"}
            </button>
            <button
              onClick={handlePublish}
              disabled={loading}
              className="flex-1 min-w-[140px] px-6 py-3 bg-green-600 hover:bg-green-700 text-white font-medium rounded-lg transition disabled:opacity-50"
            >
              {loading ? "Publishing..." : "🚀 Publish"}
            </button>
          </div>
        </div>
      </motion.div>

      <Footer />
    </div>
  );
}
