// src/pages/Login.js
import { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { EyeIcon, EyeSlashIcon } from "@heroicons/react/24/solid";

export default function Login() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [showPassword, setShowPassword] = useState(false);
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post("http://localhost:8000/auth/login", {
        email,
        password,
      });
      localStorage.setItem("token", res.data.token);
      navigate("/");
    } catch {
      alert("‚ùå Login failed! Check your credentials.");
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-r from-green-700 to-emerald-600 flex items-center justify-center px-4">
      <div className="bg-white/10 backdrop-blur-xl shadow-xl p-8 sm:p-10 rounded-2xl w-full max-w-md border border-white/20">
        {/* Logo & Heading */}
        <div className="flex flex-col items-center mb-6">
          <div className="bg-white/20 p-3 rounded-full">
            <img
              src="https://cdn-icons-png.flaticon.com/512/3064/3064155.png"
              alt="Login icon"
              className="w-16 h-16"
            />
          </div>
          <h2 className="text-3xl font-extrabold text-white text-center mt-4">
            Welcome Back
          </h2>
          <p className="text-sm text-white/80 mt-1 text-center">
            Login to continue your wellness journey üåø
          </p>
        </div>

        {/* Form */}
        <form onSubmit={handleLogin} className="space-y-5">
          {/* Email */}
          <input
            type="email"
            placeholder="Email Address"
            className="w-full px-4 py-3 border border-white/30 bg-white/10 text-white rounded-lg focus:ring-2 focus:ring-green-400 outline-none placeholder-white/70"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />

          {/* Password */}
          <div className="relative">
            <input
              type={showPassword ? "text" : "password"}
              placeholder="Password"
              className="w-full px-4 py-3 border border-white/30 bg-white/10 text-white rounded-lg focus:ring-2 focus:ring-green-400 outline-none pr-10 placeholder-white/70"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
            {showPassword ? (
              <EyeSlashIcon
                className="w-5 h-5 text-white/70 absolute top-3 right-3 cursor-pointer"
                onClick={() => setShowPassword(false)}
              />
            ) : (
              <EyeIcon
                className="w-5 h-5 text-white/70 absolute top-3 right-3 cursor-pointer"
                onClick={() => setShowPassword(true)}
              />
            )}
          </div>

          {/* Submit Button */}
          <button
            type="submit"
            className="w-full bg-green-600 hover:bg-green-700 text-white font-semibold py-3 rounded-lg transition-all shadow-lg hover:shadow-xl"
          >
            Log In
          </button>

          {/* Register Link */}
          <p className="text-sm text-center text-white/80">
            Don&apos;t have an account?{" "}
            <span
              onClick={() => navigate("/register")}
              className="text-white font-semibold underline cursor-pointer hover:text-green-300"
            >
              Register here
            </span>
          </p>
        </form>
      </div>
    </div>
  );
}
