// src/pages/Register.js
import { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

export default function Register() {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [showPassword, setShowPassword] = useState(false);
  const navigate = useNavigate();

  const handleRegister = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:8000/auth/register", {
        name,
        email,
        password,
      });
      alert("‚úÖ Registered successfully!");
      navigate("/");
    } catch {
      alert("‚ùå Registration failed! Email may already exist.");
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-indigo-500 to-purple-600 flex items-center justify-center px-4">
      <div className="bg-white/20 backdrop-blur-xl shadow-xl p-8 sm:p-10 rounded-2xl w-full max-w-md animate-fade-in">
        <div className="flex flex-col items-center mb-6">
          <img
            src="https://cdn-icons-png.flaticon.com/512/3596/3596094.png"
            alt="Register icon"
            className="w-16 h-16 mb-2"
          />
          <h2 className="text-3xl font-bold text-white text-center">
            Create an Account
          </h2>
          <p className="text-sm text-indigo-100 mt-1">
            Let's start your wellness journey ‚ú®
          </p>
        </div>
        <form onSubmit={handleRegister} className="space-y-5">
          <input
            type="text"
            placeholder="Full Name"
            className="w-full px-4 py-3 border border-white/30 bg-white/10 text-white rounded-lg focus:ring-2 focus:ring-purple-300 outline-none placeholder-white/80"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
          />
          <input
            type="email"
            placeholder="Email Address"
            className="w-full px-4 py-3 border border-white/30 bg-white/10 text-white rounded-lg focus:ring-2 focus:ring-purple-300 outline-none placeholder-white/80"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
          <div className="relative">
            <input
              type={showPassword ? "text" : "password"}
              placeholder="Create Password"
              className="w-full px-4 py-3 border border-white/30 bg-white/10 text-white rounded-lg focus:ring-2 focus:ring-purple-300 outline-none pr-10 placeholder-white/80"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
            <span
              onClick={() => setShowPassword(!showPassword)}
              className="absolute top-3 right-3 text-white/70 cursor-pointer select-none"
            >
              {showPassword ? "üôà" : "üëÅÔ∏è"}
            </span>
          </div>
          <button
            type="submit"
            className="w-full bg-purple-600 hover:bg-purple-700 text-white font-semibold py-3 rounded-lg transition-all"
          >
            Register
          </button>
          <p className="text-sm text-center text-white/80">
            Already have an account?{" "}
            <span
              onClick={() => navigate("/")}
              className="text-white font-medium underline cursor-pointer"
            >
              Log in
            </span>
          </p>
        </form>
      </div>
    </div>
  );
}
