import React, { useEffect, useState } from "react";
import { motion } from "framer-motion";
import axios from "axios";
import { FaTags, FaCalendarAlt } from "react-icons/fa";

const Dashboard = () => {
  const [sessions, setSessions] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchSessions = async () => {
      try {
        const res = await axios.get("http://localhost:8000/sessions");
        setSessions(res.data);
      } catch (err) {
        console.error(err);
      } finally {
        setLoading(false);
      }
    };
    fetchSessions();
  }, []);

  return (
    <div className="min-h-screen bg-gradient-to-br from-green-50 to-green-100 py-10 px-4">
      <h1 className="text-4xl font-bold text-center text-green-800 mb-8">
        ðŸŒ¿ Wellness Sessions
      </h1>

      {loading ? (
        <p className="text-center text-lg text-green-700 animate-pulse">
          Loading sessions...
        </p>
      ) : sessions.length === 0 ? (
        <div className="flex flex-col items-center justify-center mt-20">
          <img
            src="https://cdn-icons-png.flaticon.com/512/4076/4076549.png"
            alt="No data"
            className="w-32 h-32 mb-4 opacity-70"
          />
          <p className="text-green-700 text-lg">
            No sessions available yet. Check back later! ðŸŒ±
          </p>
        </div>
      ) : (
        <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
          {sessions.map((session) => (
            <motion.div
              key={session._id}
              whileHover={{ scale: 1.05 }}
              className="bg-white shadow-lg rounded-lg p-6 transition-all duration-300 border border-green-200"
            >
              <h2 className="text-2xl font-semibold text-green-900 mb-2">
                {session.title}
              </h2>
              <div className="flex items-center gap-2 text-sm text-green-700 mb-3">
                <FaTags />
                {session.tags.join(", ")}
              </div>
              <div className="flex items-center gap-2 text-xs text-green-500 mb-4">
                <FaCalendarAlt />
                {new Date(session.created_at).toLocaleDateString()}
              </div>
              <button className="px-4 py-2 bg-green-600 text-white rounded-lg shadow hover:bg-green-700 transition duration-200">
                View Details
              </button>
            </motion.div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Dashboard;
