// src/components/SessionCard.js
import axios from "axios";

export default function SessionCard({ session, fetchSessions }) {
  const token = localStorage.getItem("token");

  const handlePublish = async () => {
    try {
      await axios.post(
        "http://localhost:8000/sessions/my-sessions/publish",
        { id: session._id },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      fetchSessions();
    } catch (err) {
      alert("❌ Failed to publish session");
    }
  };

  const handleDelete = async () => {
    // Not part of your current API list — implement if you want
    alert("Delete functionality not implemented yet.");
  };

  return (
    <div className="bg-white/20 backdrop-blur-md p-5 rounded-xl text-white shadow-lg hover:scale-[1.02] transition-all">
      <h3 className="text-lg font-semibold mb-1">{session.title}</h3>
      <p className="text-xs text-green-100 mb-2">
        Created: {new Date(session.createdAt).toLocaleString()}
      </p>
      <span
        className={`text-xs font-semibold inline-block px-2 py-1 rounded-full ${
          session.status === "published"
            ? "bg-green-600"
            : "bg-yellow-500 text-black"
        }`}
      >
        {session.status?.toUpperCase() || "DRAFT"}
      </span>

      {/* Buttons */}
      <div className="mt-4 flex gap-2 flex-wrap">
        {session.status !== "published" && (
          <button
            onClick={handlePublish}
            className="px-3 py-1 bg-emerald-600 hover:bg-emerald-700 rounded text-sm"
          >
            Publish
          </button>
        )}
        <button
          onClick={handleDelete}
          className="px-3 py-1 bg-red-500 hover:bg-red-600 rounded text-sm"
        >
          Delete
        </button>
      </div>
    </div>
  );
}
