import React, { useState } from "react";
import Navbar from "../components/Navbar";
import Footer from "../components/Footer";
import { motion } from "framer-motion";

export default function SessionEditor() {
  const [title, setTitle] = useState("");
  const [tags, setTags] = useState("");
  const [jsonFileUrl, setJsonFileUrl] = useState("");
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState("");
  const [mobileMenuOpen, setMobileMenuOpen] = useState(false);

  const handleSaveDraft = async () => {
    if (!title || !tags || !jsonFileUrl) {
      setMessage("‚ö† Please fill all fields before saving.");
      return;
    }

    setLoading(true);
    setMessage("");

    try {
      const res = await fetch("http://localhost:8000/sessions/my-sessions/save-draft", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${localStorage.getItem("token")}`,
        },
        body: JSON.stringify({
          title,
          tags,
          json_file_url: jsonFileUrl,
        }),
      });

      const data = await res.json();

      if (res.ok) {
        setMessage("‚úÖ Draft saved successfully!");
        setTitle("");
        setTags("");
        setJsonFileUrl("");
      } else {
        setMessage(`‚ùå ${data.msg || "Failed to save draft"}`);
      }
    } catch (err) {
      setMessage("‚ùå Something went wrong. Please try again.");
    }

    setLoading(false);
  };

  return (
    <div className="bg-gray-50 min-h-screen">
      <Navbar onMobileMenuToggle={setMobileMenuOpen} />

      <motion.div
        animate={{ marginTop: mobileMenuOpen ? 220 : 64 }}
        transition={{ duration: 0.3 }}
        className="max-w-3xl mx-auto px-4 py-10"
      >
        <h1 className="text-3xl font-bold text-green-700 mb-6 text-center">
          ‚úè Create a New Session
        </h1>

        <div className="bg-white shadow-lg rounded-xl p-6 space-y-5">
          <div>
            <label className="block text-gray-700 font-medium mb-2">Session Title</label>
            <input
              type="text"
              value={title}
              onChange={(e) => setTitle(e.target.value)}
              placeholder="Enter session title"
              className="w-full border border-gray-300 rounded-lg px-4 py-2 focus:outline-none focus:border-green-500"
            />
          </div>

          <div>
            <label className="block text-gray-700 font-medium mb-2">Tags</label>
            <input
              type="text"
              value={tags}
              onChange={(e) => setTags(e.target.value)}
              placeholder="e.g. yoga, meditation, wellness"
              className="w-full border border-gray-300 rounded-lg px-4 py-2 focus:outline-none focus:border-green-500"
            />
          </div>

          <div>
            <label className="block text-gray-700 font-medium mb-2">JSON File URL</label>
            <input
              type="text"
              value={jsonFileUrl}
              onChange={(e) => setJsonFileUrl(e.target.value)}
              placeholder="Enter JSON file link"
              className="w-full border border-gray-300 rounded-lg px-4 py-2 focus:outline-none focus:border-green-500"
            />
          </div>

          {message && (
            <motion.div
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              className={`p-3 rounded-lg text-sm ${
                message.startsWith("‚úÖ") ? "bg-green-100 text-green-700" : "bg-red-100 text-red-700"
              }`}
            >
              {message}
            </motion.div>
          )}

          <div className="flex justify-center">
            <button
              onClick={handleSaveDraft}
              disabled={loading}
              className="px-6 py-2 bg-green-600 hover:bg-green-700 text-white rounded-lg transition disabled:opacity-50"
            >
              {loading ? "Saving..." : "üíæ Save as Draft"}
            </button>
          </div>
        </div>
      </motion.div>

      <Footer />
    </div>
  );
}
