// src/pages/SessionEditor.js
import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import Navbar from "../components/Navbar";
import Footer from "../components/Footer";
import { motion } from "framer-motion";

export default function SessionEditor() {
  const { id } = useParams();
  const navigate = useNavigate();

  const [title, setTitle] = useState("");
  const [tags, setTags] = useState("");
  const [jsonFileUrl, setJsonFileUrl] = useState("");
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState("");
  const [mobileMenuOpen, setMobileMenuOpen] = useState(false);
  const [isEdit, setIsEdit] = useState(false);

  const token = localStorage.getItem("token");

  // Fetch existing session if editing
  useEffect(() => {
    if (id) {
      setIsEdit(true);
      fetch(`http://localhost:8000/sessions/my-sessions/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      })
        .then((res) => res.json())
        .then((data) => {
          if (data) {
            setTitle(data.title || "");
            setTags(data.tags?.join(",") || "");
            setJsonFileUrl(data.json_file_url || "");
          }
        })
        .catch((err) => console.error("Error fetching session:", err));
    }
  }, [id, token]);

  // Save Draft or Publish handler
  const handleSave = async (publish = false) => {
    if (!title || !tags || !jsonFileUrl) {
      setMessage("⚠ Please fill all fields before saving.");
      return;
    }

    setLoading(true);
    setMessage("");

    try {
      if (isEdit) {
        // Update existing draft
        const res = await fetch(`http://localhost:8000/sessions/my-sessions/${id}`, {
          method: "PUT",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${token}`,
          },
          body: JSON.stringify({
            title,
            tags,
            json_file_url: jsonFileUrl,
          }),
        });

        if (!res.ok) throw new Error("Failed to update session");

        if (publish) {
          await fetch("http://localhost:8000/sessions/my-sessions/publish", {
            method: "POST",
            headers: {
              "Content-Type": "application/json",
              Authorization: `Bearer ${token}`,
            },
            body: JSON.stringify({ id }),
          });
          setMessage("✅ Session updated & published successfully!");
        } else {
          setMessage("✅ Draft updated successfully!");
        }
      } else {
        // Create a new draft
        const res = await fetch("http://localhost:8000/sessions/my-sessions/save-draft", {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${token}`,
          },
          body: JSON.stringify({
            title,
            tags,
            json_file_url: jsonFileUrl,
          }),
        });

        const data = await res.json();
        if (!res.ok) throw new Error(data.msg || "Failed to save draft");

        if (publish) {
          await fetch("http://localhost:8000/sessions/my-sessions/publish", {
            method: "POST",
            headers: {
              "Content-Type": "application/json",
              Authorization: `Bearer ${token}`,
            },
            body: JSON.stringify({ id: data._id }),
          });
          setMessage("✅ Session published successfully!");
        } else {
          setMessage("✅ Draft saved successfully!");
        }

        setTitle("");
        setTags("");
        setJsonFileUrl("");
      }
    } catch (err) {
      setMessage("❌ " + err.message);
    }

    setLoading(false);
  };

  return (
    <div className="bg-gray-50 min-h-screen flex flex-col">
      <Navbar onMobileMenuToggle={setMobileMenuOpen} />

      <motion.div
        animate={{ marginTop: mobileMenuOpen ? 220 : 64 }}
        transition={{ duration: 0.3 }}
        className="flex-1 flex justify-center"
      >
        <div className="w-full max-w-4xl px-4 py-10">
          <h1 className="text-3xl font-bold text-green-700 mb-6 text-center">
            {isEdit ? "✏ Edit Session" : "🆕 Create a New Session"}
          </h1>

          <div className="bg-white shadow-lg rounded-xl p-6 space-y-5">
            <div>
              <label className="block text-gray-700 font-medium mb-2">
                Session Title
              </label>
              <input
                type="text"
                value={title}
                onChange={(e) => setTitle(e.target.value)}
                placeholder="Enter session title"
                className="w-full border border-gray-300 rounded-lg px-4 py-2 focus:outline-none focus:border-green-500"
              />
            </div>

            <div>
              <label className="block text-gray-700 font-medium mb-2">
                Tags
              </label>
              <input
                type="text"
                value={tags}
                onChange={(e) => setTags(e.target.value)}
                placeholder="e.g. yoga, meditation, wellness"
                className="w-full border border-gray-300 rounded-lg px-4 py-2 focus:outline-none focus:border-green-500"
              />
            </div>

            <div>
              <label className="block text-gray-700 font-medium mb-2">
                JSON File URL
              </label>
              <input
                type="text"
                value={jsonFileUrl}
                onChange={(e) => setJsonFileUrl(e.target.value)}
                placeholder="Enter JSON file link"
                className="w-full border border-gray-300 rounded-lg px-4 py-2 focus:outline-none focus:border-green-500"
              />
            </div>

            {message && (
              <motion.div
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                className={`p-3 rounded-lg text-sm ${
                  message.startsWith("✅")
                    ? "bg-green-100 text-green-700"
                    : "bg-red-100 text-red-700"
                }`}
              >
                {message}
              </motion.div>
            )}

            <div className="flex justify-center gap-4">
              <button
                onClick={() => handleSave(false)}
                disabled={loading}
                className="px-6 py-2 bg-yellow-500 hover:bg-yellow-600 text-white rounded-lg transition disabled:opacity-50"
              >
                {loading ? "Saving..." : "💾 Save as Draft"}
              </button>
              <button
                onClick={() => handleSave(true)}
                disabled={loading}
                className="px-6 py-2 bg-green-600 hover:bg-green-700 text-white rounded-lg transition disabled:opacity-50"
              >
                {loading ? "Publishing..." : "🚀 Publish"}
              </button>
            </div>
          </div>
        </div>
      </motion.div>

      <Footer />
    </div>
  );
}
