import axios from "axios";

export default function SessionCard({ session, fetchSessions }) {
  const token = localStorage.getItem("token");

  const handlePublish = async () => {
    try {
      await axios.post(
        "http://localhost:8000/sessions/my-sessions/publish",
        { id: session._id },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      fetchSessions();
    } catch (err) {
      alert("❌ Failed to publish session");
    }
  };

  return (
    <div className="bg-white p-5 rounded-xl border shadow hover:scale-[1.02] transition-all">
      <h3 className="text-lg font-semibold text-green-800">{session.title}</h3>
      <p className="text-sm text-gray-600 mt-1 mb-2">{session.tags?.join(", ")}</p>
      <span
        className={`text-xs font-semibold inline-block px-2 py-1 rounded-full ${
          session.status === "published"
            ? "bg-green-500 text-white"
            : "bg-yellow-400 text-black"
        }`}
      >
        {session.status?.toUpperCase()}
      </span>

      <div className="mt-4 flex gap-2">
        {session.status !== "published" && (
          <button
            onClick={handlePublish}
            className="bg-green-600 text-white px-3 py-1 rounded text-sm hover:bg-green-700"
          >
            Publish
          </button>
        )}
        {/* Optional: Add delete or view later */}
      </div>
    </div>
  );
}
