import React, { useEffect, useState } from "react";
import Navbar from "../components/Navbar";
import Footer from "../components/Footer";
import { motion } from "framer-motion";

const Dashboard = () => {
  const [sessions, setSessions] = useState([]);
  const [mobileMenuOpen, setMobileMenuOpen] = useState(false);

  useEffect(() => {
    fetch("http://localhost:8000/sessions")
      .then((res) => res.json())
      .then((data) => setSessions(data))
      .catch((err) => console.error(err));
  }, []);

  return (
    <div className="bg-gray-50 min-h-screen">
      {/* Navbar */}
      <Navbar onMobileMenuToggle={setMobileMenuOpen} />

      {/* Animated main content shift */}
      <motion.div
        animate={{ marginTop: mobileMenuOpen ? 220 : 64 }}
        transition={{ duration: 0.3 }}
      >
        {/* Hero Carousel */}
        <div className="relative w-full overflow-hidden">
          <div className="carousel w-full h-[300px] md:h-[500px] relative">
            <div className="carousel-item absolute w-full animate-fade-slide">
              <img src="/hero1.png" alt="Yoga" className="w-full h-full object-cover" />
            </div>
            <div className="carousel-item absolute w-full animate-fade-slide delay-2000">
              <img src="/hero1.png" alt="Meditation" className="w-full h-full object-cover" />
            </div>
            <div className="carousel-item absolute w-full animate-fade-slide delay-4000">
              <img src="/hero1.png" alt="Wellness" className="w-full h-full object-cover" />
            </div>
          </div>
        </div>

        {/* Published Sessions */}
        <section className="container mx-auto py-10 px-4">
          <h2 className="text-3xl font-bold mb-6 text-green-700">
            ðŸŒ¿ Published Sessions
          </h2>
          {sessions.length === 0 ? (
            <p className="text-gray-500">No sessions available yet.</p>
          ) : (
            <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
              {sessions.map((session) => (
                <motion.div
                  key={session._id}
                  className="bg-white rounded-lg shadow-lg p-4 hover:shadow-2xl transition"
                  whileHover={{ scale: 1.05 }}
                >
                  <h3 className="text-xl font-semibold text-green-700">
                    {session.title}
                  </h3>
                  <p className="text-gray-500 mt-2">
                    Tags: {session.tags.join(", ")}
                  </p>
                  <a
                    href={session.json_file_url}
                    target="_blank"
                    rel="noreferrer"
                    className="text-blue-500 underline mt-3 block"
                  >
                    View Session Details
                  </a>
                </motion.div>
              ))}
            </div>
          )}
        </section>

        {/* Footer */}
        <Footer />
      </motion.div>
    </div>
  );
};

export default Dashboard;
